</template>
<script>
import Popup from '../InputComponents/Popup.vue'
import Buttons from '../InputComponents/Buttons.vue'
export default { 
    name: 'vraag4',
    components:{
        Buttons,
        Popup
    },
    data(){
        return{
            keuzeGemaakt: false,
            pageChoices:{
                values: ['Klaar'],
                btnClass: 'button1',
                next: '/veilig',
                eventFunction: this.showPopup,
                btnCls: 'button2'
            },
            popupShow: false,
            popupProperties:{
                antwoord: '',
                event: this.gekozen
            },
            antwoord: '',
            hoeveel: 100,
            over: 0
            
        }
    },
    methods:{
        showPopup(){
            if(this.hoeveel == 27){
                this.popupProperties.antwoord = "Dat klopt!"
            }else{
                this.popupProperties.antwoord = "Helaas!"
            }
            this.popupShow = true;
        },
        gekozen(){
            this.popupShow = false
            this.keuzeGemaakt = true
        },
        sendInfo(){
            console.log("sending info")
            this.$emit('userInput', 'Vraag4', this.hoeveel)
        },
        plus(){
            if(this.hoeveel <100){
                this.hoeveel ++
            }
        },
        minus(){
            if(this.hoeveel >0){                    
                this.hoeveel -- 
            }
        },
    },
    created(){
        document.querySelector("body").style.background = '#023274'
    }
    
}
</script>
